import React from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends React.Component {

  state = {
    lista: [],
    itemPagina: 0
  }

  async componentDidMount() {
    var dados = await this.pegarDados();
    this.setState({ lista: dados.data })
  }

  async pegarDados(filtro) {
    var requisicao = await axios.get('https://kitsu.io/api/edge/characters?page[limit]=5&' +
      'page[offset]=' + this.state.itemPagina+
      (filtro?'&filter[name]='+filtro : '') );
    return requisicao.data;
  }

  async filtrar(filtro) {
    var dados = await this.pegarDados(filtro);
    this.setState({ lista: dados.data })
  }

  voltarPagina() {
    this.setState({ itemPagina: this.state.itemPagina - 5 }, this.filtrar);
  }
  avancaPagina() {
    this.setState({ itemPagina: this.state.itemPagina + 5 }, this.filtrar);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1>SITE BOLADO</h1>
          <input onChange={(acao)=> this.filtrar(acao.target.value)}/>

          <ul>
            {this.state.lista.map(item => (
              <li style={{ height: 75, display: 'flex', border: '1px solid white', padding: 10, marginBottom: 5 }}>
                <div style={{ display: 'flex', flex: 2 }}>
                  <img src={item.attributes.image? item.attributes.image.original : ''} height="75"></img>
                  {item.attributes.name}
                </div>
                <div style={{ overflow: 'auto', flex: 3 }}>
                  {item.attributes.description}
                </div>
              </li>
            ))}
          </ul>
          <div style={{ display: 'flex' }}>
            <button onClick={this.voltarPagina.bind(this)}>Voltar</button>
            <div>
              {(this.state.itemPagina / 5) + 1}
            </div>
            <button onClick={this.avancaPagina.bind(this)}>Avan√ßar</button>
          </div>
        </header>
      </div>
    );
  }

}

export default App;
